import React, { Component } from 'react';
import { MuiThemeProvider, createMuiTheme } from 'material-ui/styles';
import './App.css';
import Signup from './Signup';
import Login from './Login';
import RaisedButton from 'material-ui/RaisedButton';
class App extends Component {

  state = {
    fields_signup: {},
    fields_login: {},
  }

  onChange = (updatedValue) =>{
    this.setState({
      fields_signup: {
        ...this.state.fields_signup,
        ...updatedValue
      }
    });
    //console.log("App Component got: ", fields);
  };

  onSubmit = (e) => {
    console.log("LOL");
  }


  render() {
    var toggle_signin_login = true;
    if(toggle_signin_login){ 
      return (
        <MuiThemeProvider>    
          <div className="App">
            <RaisedButton onClick={e=>this.onSubmit(e)} label="Create a new Account" primary/>
            <div class="signup">
              <Signup onChange={fields_signup=>this.onChange(fields_signup)}/>
            </div>
            <p>
              {JSON.stringify(this.state.fields_signup, null, 2)}
            </p>
          </div>
        </MuiThemeProvider>
      );
    }else{
      return (
        <MuiThemeProvider>    
          <div className="App">
            <RaisedButton onClick={e=>this.onSubmit(e)} label="Create a new Account" primary/>
            <div class="login">
              <Signup onChange={fields_login=>this.onChange(fields_login)}/>
            </div>
            <p>
              {JSON.stringify(this.state.fields_login, null, 2)}
            </p>
          </div>
        </MuiThemeProvider>
      );
    }
  }
}

export default App;
