import React, { Component } from 'react';
import { MuiThemeProvider, createMuiTheme } from 'material-ui/styles';
import './App.css';
import Signup from './Signup';
import Login from './Login';
import Home from './Home';
import RaisedButton from 'material-ui/RaisedButton';
import Cookies from 'universal-cookie';
import {
  BrowserView,
  MobileView,
  isBrowser,
  isMobile
} from "react-device-detect";

var toggle_signin_login = true;
export default class App extends Component {

  state = {
    fields_signup: {},
    fields_login: {},
  }

  onChangeSignup = (updatedValue) =>{
    this.setState({
      fields_signup: {
        ...this.state.fields_signup,
        ...updatedValue
      }
    });
    //console.log("App Component got: ", fields);
  };

  onChangeLogin = (updatedValue) =>{
    this.setState({
      fields_login: {
        ...this.state.fields_login,
        ...updatedValue
      }
    });
    //console.log("App Component got: ", fields);
  };

  onSubmit = (e) => {
    if(toggle_signin_login==true)
      toggle_signin_login = false;
    else
      toggle_signin_login = true;
    this.forceUpdate();
    //console.log(toggle_signin_login);
  }

  validateCookieUUID = (UUID) => {
    //console.log(UUID);
    fetch(`http://checkcookie/existence`)
      //.then(response=>response.json())
      .then(response=>console.error(response))
      .catch(err=>console.error(err))
  }

  renderMobileContent = () => {
    if (isMobile) {
      return (<div> This content is unavailable on mobile</div>);
    }
  }

  render() {

    return this.renderMobileContent();

    const cookies = new Cookies();
    //cookies.set('UUID', 'LOL', { path: '/' });

    var UUID = cookies.get('UUID');
    if(UUID){
      this.validateCookieUUID(UUID);
      return(<Home/>);
    }


  }
}

//export default App;
