import React, { Component } from 'react';
import { MuiThemeProvider, createMuiTheme } from 'material-ui/styles';
import './App.css';
import Signup from './Signup';
import Login from './Login';
import RaisedButton from 'material-ui/RaisedButton';
var toggle_signin_login = true;
class App extends Component {

  state = {
    fields_signup: {},
    fields_login: {},
  }

  onChange = (updatedValue) =>{
    this.setState({
      fields_signup: {
        ...this.state.fields_signup,
        ...updatedValue
      }
    });
    //console.log("App Component got: ", fields);
  };

  onSubmit = (e) => {
    if(toggle_signin_login==true)
      toggle_signin_login = false;
    else
      toggle_signin_login = true;
    console.log(toggle_signin_login);
  }


  render() { 
      return (
        <MuiThemeProvider>    
          <div className="App">
            <RaisedButton onClick={e=>this.onSubmit(e)} label="Already have an Account" primary/>
            <div className={toggle_signin_login ? 'hidden' : ''}>
              <Signup onChange={fields_signup=>this.onChange(fields_signup)}/>
            </div>
            <p>
              {JSON.stringify(this.state.fields_signup, null, 2)}
            </p>
          </div>
        </MuiThemeProvider>
      );
  }
}

export default App;
